        Pattern pattern = Pattern.compile(expression);
        Matcher matcher = pattern.matcher(text); // ходи по индексите на стринга, където има съвпадение

        while (matcher.find()){ //nameri sledvashtoto i ako ima go printirai
            System.out.println(matcher.group());
        }

